mapper.select:
SELECT goodType.name AS goodType,
       good.goodTypeId AS goodtypeId,
       good.name AS name,
       profile,
       price,
       good.goodId AS goodId,
       stock,
       soldSum,
       onSale,
       detail,
       detailImgId as detailsImg
FROM good,goodType

    good.goodTypeId=goodType.goodTypeId

        and good.goodId = #{goodId}


        and good.name like #{keyword}


        and goodType.name like #{searchType}


        and good.price >= #{minPrice}


        and good.price <= #{maxPrice}


LIMIT #{num},#{size}

SELECT COUNT(*)
FROM good,goodType

    good.goodTypeId=goodType.goodTypeId

        and good.name like #{keyword}


        and goodType.name like #{searchType}


        and good.price >= #{minPrice}


        and good.price <= #{maxPrice}


        and good.goodId = #{goodId}
